import React, { useState } from 'react';
// 游녣 IMPORTACI칍N DEL CSS
import '../estilos/estilo.css';

export default function ClienteTramite() {
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [tramites, setTramites] = useState([]);
  const [mensaje, setMensaje] = useState('');

  const handleGenerar = async e => {
    e.preventDefault();
    setMensaje('');
    setTramites([]);

    try {
      const res = await fetch('http://127.0.0.1:5000/generar-tramite', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({nombre, correo})
      });
      const data = await res.json();
      if(res.ok){
        if(data.tramites.length === 0){
          setMensaje("No se encontraron tr치mites con esos datos.");
        } else {
          setTramites(data.tramites);
        }
      } else {
        setMensaje(data.error || "Error al consultar tr치mites.");
      }
    } catch(err){
      setMensaje(`Error: ${err.message}`);
    }
  }

  // L칩gica para determinar si el mensaje es un error o solo informativo
  const isError = mensaje.startsWith('Error') || mensaje.includes('No se encontraron');

  return (
    // Aplicamos la clase CSS del contenedor
    <div className={styles.container}> 
      <h2 className={styles.title}>Consultar Tr치mite</h2>
      
      {/* Aplicamos la clase CSS al formulario */}
      <form onSubmit={handleGenerar} className={styles.form}>
        <input 
          type="text" 
          placeholder="Nombre" 
          value={nombre} 
          onChange={e=>setNombre(e.target.value)} 
          required 
          className={styles.input} // Clase CSS para input
        />
        <input 
          type="email" 
          placeholder="Correo Electr칩nico" 
          value={correo} 
          onChange={e=>setCorreo(e.target.value)} 
          required 
          className={styles.input} // Clase CSS para input
        />
        <button 
          type="submit" 
          className={styles.button} // Clase CSS para el bot칩n
        >
          Consultar Tr치mites
        </button>
      </form>

      {/* Aplicamos clases condicionales al mensaje */}
      {mensaje && (
        <p className={`${styles.message} ${isError ? styles.errorMessage : styles.infoMessage}`}>
          {mensaje}
        </p>
      )}

      {tramites.length>0 && (
        <div className={styles.tableContainer}>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Tipo</th>
                <th>Estado</th>
                <th>Fecha Solicitud</th>
              </tr>
            </thead>
            <tbody>
              {tramites.map(t=>(
                <tr key={t.id}>
                  <td>{t.id}</td>
                  <td>{t.tipo_tramite}</td>
                  <td>{t.estado}</td>
                  <td>{t.fecha_solicitud}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}
