import React, { useState } from 'react';

export default function ClienteTramite() {
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [tramites, setTramites] = useState([]);
  const [mensaje, setMensaje] = useState('');

  const handleGenerar = async e => {
    e.preventDefault();
    setMensaje('');
    setTramites([]);

    try {
      const res = await fetch('http://127.0.0.1:5000/generar-tramite', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({nombre, correo})
      });
      const data = await res.json();
      if(res.ok){
        if(data.tramites.length === 0){
          setMensaje("No se encontraron trámites con esos datos.");
        } else {
          setTramites(data.tramites);
        }
      } else {
        setMensaje(data.error || "Error al consultar trámites.");
      }
    } catch(err){
      setMensaje(`Error: ${err.message}`);
    }
  }

  return (
    <div className="bg-gradient-to-br from-blue-50 to-blue-100 min-h-screen flex justify-center items-start p-10">
      <div className="bg-white p-8 rounded-3xl shadow-2xl w-full max-w-2xl">
        <h2 className="text-3xl font-bold text-blue-900 mb-6 text-center">Consultar Trámite</h2>
        <form onSubmit={handleGenerar} className="flex flex-col gap-4">
          <input
            type="text"
            placeholder="Nombre"
            value={nombre}
            onChange={e => setNombre(e.target.value)}
            required
            className="p-3 rounded-lg border border-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-200 w-full"
          />
          <input
            type="email"
            placeholder="Correo"
            value={correo}
            onChange={e => setCorreo(e.target.value)}
            required
            className="p-3 rounded-lg border border-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-200 w-full"
          />
          <button
            type="submit"
            className="bg-blue-400 hover:bg-blue-500 text-white font-semibold py-3 px-6 rounded-lg transition-colors"
          >
            Generar Trámite
          </button>
        </form>

        {mensaje && <p className="text-red-500 mt-4">{mensaje}</p>}

        {tramites.length > 0 && (
          <div className="overflow-x-auto mt-6">
            <table className="table-auto w-full border-collapse text-left">
              <thead>
                <tr className="bg-blue-200 text-blue-900 font-semibold">
                  <th className="border border-blue-300 px-4 py-2">ID</th>
                  <th className="border border-blue-300 px-4 py-2">Tipo</th>
                  <th className="border border-blue-300 px-4 py-2">Estado</th>
                  <th className="border border-blue-300 px-4 py-2">Fecha Solicitud</th>
                </tr>
              </thead>
              <tbody>
                {tramites.map(t => (
                  <tr key={t.id} className="even:bg-blue-50 hover:bg-blue-100 transition-colors">
                    <td className="border border-blue-300 px-4 py-2">{t.id}</td>
                    <td className="border border-blue-300 px-4 py-2">{t.tipo_tramite}</td>
                    <td className="border border-blue-300 px-4 py-2">{t.estado}</td>
                    <td className="border border-blue-300 px-4 py-2">{t.fecha_solicitud}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}
