import React, { useState } from 'react';

export default function ClienteTramite() {
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [tramites, setTramites] = useState([]);
  const [mensaje, setMensaje] = useState('');

  const handleGenerar = async e => {
    e.preventDefault();
    setMensaje('');
    setTramites([]);

    try {
      const res = await fetch('http://127.0.0.1:5000/generar-tramite', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({nombre, correo})
      });
      const data = await res.json();
      if(res.ok){
        if(data.tramites.length === 0){
          setMensaje("No se encontraron trámites con esos datos.");
        } else {
          setTramites(data.tramites);
        }
      } else {
        setMensaje(data.error || "Error al consultar trámites.");
      }
    } catch(err){
      setMensaje(`Error: ${err.message}`);
    }
  }

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow-lg rounded-lg mt-10">
      <h2 className="text-2xl font-bold mb-6 text-gray-800">Consultar Trámite</h2>
      <form onSubmit={handleGenerar} className="space-y-4">
        <input 
          type="text" 
          placeholder="Nombre" 
          value={nombre} 
          onChange={e=>setNombre(e.target.value)} 
          required 
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <input 
          type="email" 
          placeholder="Correo" 
          value={correo} 
          onChange={e=>setCorreo(e.target.value)} 
          required 
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
        <button 
          type="submit" 
          className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition-colors font-semibold"
        >
          Generar Trámite
        </button>
      </form>

      {mensaje && <p className="mt-4 text-red-500 font-medium">{mensaje}</p>}

      {tramites.length > 0 && (
        <div className="overflow-x-auto mt-6">
          <table className="min-w-full bg-gray-50 border border-gray-200 rounded-lg">
            <thead className="bg-blue-500 text-white">
              <tr>
                <th className="py-2 px-4 text-left">ID</th>
                <th className="py-2 px-4 text-left">Tipo</th>
                <th className="py-2 px-4 text-left">Estado</th>
                <th className="py-2 px-4 text-left">Fecha Solicitud</th>
              </tr>
            </thead>
            <tbody className="text-gray-700">
              {tramites.map(t => (
                <tr key={t.id} className="border-t hover:bg-gray-100 transition-colors">
                  <td className="py-2 px-4">{t.id}</td>
                  <td className="py-2 px-4">{t.tipo_tramite}</td>
                  <td className="py-2 px-4">{t.estado}</td>
                  <td className="py-2 px-4">{t.fecha_solicitud}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}
