import React, { useState } from 'react';

export default function ClienteTramite() {
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [tramites, setTramites] = useState([]);
  const [mensaje, setMensaje] = useState('');

  const handleGenerar = async e => {
    e.preventDefault();
    setMensaje('');
    setTramites([]);

    try {
      const res = await fetch('http://127.0.0.1:5000/generar-tramite', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({nombre, correo})
      });
      const data = await res.json();
      if(res.ok){
        if(data.tramites.length === 0){
          setMensaje("No se encontraron tr치mites con esos datos.");
        } else {
          setTramites(data.tramites);
        }
      } else {
        setMensaje(data.error || "Error al consultar tr치mites.");
      }
    } catch(err){
      setMensaje(`Error: ${err.message}`);
    }
  }

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow-xl rounded-lg mt-10">
      <h2 className="text-3xl font-bold text-gray-800 mb-6 border-b pb-2">Consultar Tr치mite 游닇</h2>
      
      {/* Formulario de Consulta */}
      <form onSubmit={handleGenerar} className="space-y-4 mb-6">
        <input 
          type="text" 
          placeholder="Nombre" 
          value={nombre} 
          onChange={e=>setNombre(e.target.value)} 
          required 
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-150"
        />
        <input 
          type="email" 
          placeholder="Correo Electr칩nico" 
          value={correo} 
          onChange={e=>setCorreo(e.target.value)} 
          required 
          className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-150"
        />
        <button 
          type="submit" 
          className="w-full bg-blue-600 text-white font-semibold py-3 rounded-lg hover:bg-blue-700 transition duration-200 ease-in-out shadow-md"
        >
          Consultar Tr치mites
        </button>
      </form>

      {/* Mensajes de Alerta */}
      {mensaje && (
        <p className={`p-3 rounded-lg ${mensaje.startsWith('Error') ? 'bg-red-100 text-red-700 border border-red-300' : 'bg-yellow-100 text-yellow-700 border border-yellow-300'} mb-6 font-medium`}>
          {mensaje}
        </p>
      )}

      {/* Tabla de Resultados */}
      {tramites.length > 0 && (
        <div className="overflow-x-auto shadow-lg rounded-lg">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-blue-600 text-white">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-semibold uppercase tracking-wider rounded-tl-lg">ID</th>
                <th className="px-6 py-3 text-left text-xs font-semibold uppercase tracking-wider">Tipo</th>
                <th className="px-6 py-3 text-left text-xs font-semibold uppercase tracking-wider">Estado</th>
                <th className="px-6 py-3 text-left text-xs font-semibold uppercase tracking-wider rounded-tr-lg">Fecha Solicitud</th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {tramites.map(t=>(
                <tr key={t.id} className="hover:bg-gray-50 transition duration-150">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{t.id}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-700">{t.tipo_tramite}</td>
                  <td className={`px-6 py-4 whitespace-nowrap text-sm font-semibold ${
                    t.estado === 'Completado' ? 'text-green-600' : 
                    t.estado === 'En Proceso' ? 'text-yellow-600' : 
                    'text-red-600'
                  }`}>
                    {t.estado}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{t.fecha_solicitud}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}
