import React, { useState } from 'react';

export default function ClienteTramite() {
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [tramites, setTramites] = useState([]);
  const [mensaje, setMensaje] = useState('');

  const handleGenerar = async e => {
    e.preventDefault();
    setMensaje('');
    setTramites([]);

    try {
      const res = await fetch('http://127.0.0.1:5000/generar-tramite', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({nombre, correo})
      });
      const data = await res.json();
      if(res.ok){
        if(data.tramites.length === 0){
          setMensaje("No se encontraron trámites con esos datos.");
        } else {
          setTramites(data.tramites);
        }
      } else {
        setMensaje(data.error || "Error al consultar trámites.");
      }
    } catch(err){
      setMensaje(`Error: ${err.message}`);
    }
  }

  return (
    <div style={{padding:'20px'}}>
      <h2>Consultar Trámite</h2>
      <form onSubmit={handleGenerar}>
        <input type="text" placeholder="Nombre" value={nombre} onChange={e=>setNombre(e.target.value)} required /><br/>
        <input type="email" placeholder="Correo" value={correo} onChange={e=>setCorreo(e.target.value)} required /><br/>
        <button type="submit">Generar Trámite</button>
      </form>

      {mensaje && <p>{mensaje}</p>}

      {tramites.length>0 && (
        <table border="1" style={{marginTop:'20px'}}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Tipo</th>
              <th>Estado</th>
              <th>Fecha Solicitud</th>
            </tr>
          </thead>
          <tbody>
            {tramites.map(t=>(
              <tr key={t.id}>
                <td>{t.id}</td>
                <td>{t.tipo_tramite}</td>
                <td>{t.estado}</td>
                <td>{t.fecha_solicitud}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  )
}
