import React, { useState } from 'react';

export default function ClienteTramite() {
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [tramites, setTramites] = useState([]);
  const [mensaje, setMensaje] = useState('');

  const handleGenerar = async e => {
    e.preventDefault();
    setMensaje('');
    setTramites([]);

    try {
      const res = await fetch('http://127.0.0.1:5000/generar-tramite', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({nombre, correo})
      });
      const data = await res.json();
      if(res.ok){
        if(data.tramites.length === 0){
          setMensaje("No se encontraron trámites con esos datos.");
        } else {
          setTramites(data.tramites);
        }
      } else {
        setMensaje(data.error || "Error al consultar trámites.");
      }
    } catch(err){
      setMensaje(`Error: ${err.message}`);
    }
  }

  return (
    <div className="p-6 bg-blue-50 min-h-screen">
      <h2 className="text-2xl font-bold text-blue-700 mb-4">Consultar Trámite</h2>
      <form className="flex flex-col gap-3" onSubmit={handleGenerar}>
        <input 
          type="text" 
          placeholder="Nombre" 
          value={nombre} 
          onChange={e=>setNombre(e.target.value)} 
          required
          className="p-2 border border-blue-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-200"
        />
        <input 
          type="email" 
          placeholder="Correo" 
          value={correo} 
          onChange={e=>setCorreo(e.target.value)} 
          required
          className="p-2 border border-blue-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-200"
        />
        <button 
          type="submit"
          className="bg-blue-400 hover:bg-blue-500 text-white font-semibold py-2 rounded transition-colors"
        >
          Generar Trámite
        </button>
      </form>

      {mensaje && <p className="mt-4 text-blue-700 font-medium">{mensaje}</p>}

      {tramites.length > 0 && (
        <table className="mt-6 w-full border border-blue-200 rounded text-left shadow-sm">
          <thead className="bg-blue-100 text-blue-700">
            <tr>
              <th className="p-2 border-b border-blue-200">ID</th>
              <th className="p-2 border-b border-blue-200">Tipo</th>
              <th className="p-2 border-b border-blue-200">Estado</th>
              <th className="p-2 border-b border-blue-200">Fecha Solicitud</th>
            </tr>
          </thead>
          <tbody>
            {tramites.map(t => (
              <tr key={t.id} className="even:bg-blue-50 odd:bg-white">
                <td className="p-2 border-b border-blue-200">{t.id}</td>
                <td className="p-2 border-b border-blue-200">{t.tipo_tramite}</td>
                <td className="p-2 border-b border-blue-200">{t.estado}</td>
                <td className="p-2 border-b border-blue-200">{t.fecha_solicitud}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  )
}
