import React, { useState } from 'react';
// La importaci칩n directa solo aplica el CSS globalmente
import '../estilos/estilo.css'; 

export default function ClienteTramite() {
  const [nombre, setNombre] = useState('');
  const [correo, setCorreo] = useState('');
  const [tramites, setTramites] = useState([]);
  const [mensaje, setMensaje] = useState('');

  const handleGenerar = async e => {
    e.preventDefault();
    setMensaje('');
    setTramites([]);

    try {
      const res = await fetch('http://127.0.0.1:5000/generar-tramite', {
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body: JSON.stringify({nombre, correo})
      });
      const data = await res.json();
      if(res.ok){
        if(data.tramites.length === 0){
          setMensaje("No se encontraron tr치mites con esos datos.");
        } else {
          setTramites(data.tramites);
        }
      } else {
        setMensaje(data.error || "Error al consultar tr치mites.");
      }
    } catch(err){
      setMensaje(`Error: ${err.message}`);
    }
  }

  // L칩gica para aplicar clases condicionales a los mensajes
  const isError = mensaje.startsWith('Error') || mensaje.includes('No se encontraron');
  // Se asume que tienes estas clases definidas en tu estilo.css
  const messageClass = isError ? 'errorMessage' : 'infoMessage'; 

  return (
    // 游녣 NO USAR styles.container, USAR SOLAMENTE "container"
    <div className="container"> 
      <h2 className="title">Consultar Tr치mite</h2>
      
      <form onSubmit={handleGenerar} className="form">
        <input 
          type="text" 
          placeholder="Nombre" 
          value={nombre} 
          onChange={e=>setNombre(e.target.value)} 
          required 
          className="input" // 游녣 USAR SOLAMENTE "input"
        />
        <input 
          type="email" 
          placeholder="Correo Electr칩nico" 
          value={correo} 
          onChange={e=>setCorreo(e.target.value)} 
          required 
          className="input" // 游녣 USAR SOLAMENTE "input"
        />
        <button 
          type="submit" 
          className="button" // 游녣 USAR SOLAMENTE "button"
        >
          Consultar Tr치mites
        </button>
      </form>

      {mensaje && (
        // Combinamos la clase base "message" con la clase condicional
        <p className={`message ${messageClass}`}> 
          {mensaje}
        </p>
      )}

      {tramites.length>0 && (
        <div className="tableContainer">
          <table className="table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Tipo</th>
                <th>Estado</th>
                <th>Fecha Solicitud</th>
              </tr>
            </thead>
            <tbody>
              {tramites.map(t=>(
                <tr key={t.id}>
                  <td>{t.id}</td>
                  <td>{t.tipo_tramite}</td>
                  <td>{t.estado}</td>
                  <td>{t.fecha_solicitud}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  )
}
